para.screen:
server {
  listen               80;
  client_max_body_size 10M; # Default 1M, change it!

  # Serve static content
  # Root points to your DHIS webapp location, update it!

  location ~ (\.js$|\.css$|\.gif$|\.woff$|\.ttf$|\.eot$|^/images/|^/icons/|^/dhis-web-commons/.*\.png$) {
    root     /home/dhis/tomcat/webapps/ROOT;
    expires  4d;
  } 

  # Proxy pass to servlet container

  location / {
    proxy_pass        http://localhost:8080/;
    proxy_redirect    off;
    proxy_set_header  Host            $host;
    proxy_set_header  X-Real-IP       $remote_addr;
    proxy_set_header  X-Forwarded-For $proxy_add_x_forwarded_for;
  }
}

section.screen:
# Rewrite block to force use of SSL

server {
  listen     80;
  rewrite    ^ https://<server-ip>$request_uri? permanent;
}

# SSL server block

server {
  listen               443;
  client_max_body_size 10M;

  ssl                  on;
  ssl_certificate      server.crt;
  ssl_certificate_key  server.key;

  ssl_session_timeout  5m;

  ssl_protocols              SSLv2 SSLv3 TLSv1;
  ssl_ciphers                HIGH:!aNULL:!MD5;
  ssl_prefer_server_ciphers  on;

  # Root points to your DHIS webapp location, update it!

  location ~ (\.js$|\.css$|\.gif$|\.woff$|\.ttf$|\.eot$|^/images/|^/icons/|^/dhis-web-commons/.*\.png$) {
    root     /home/dhis/tomcat/webapps/ROOT;
    expires  4d;
  } 

  location / {
    proxy_pass        http://localhost:8080/;
    proxy_redirect    off;
    proxy_set_header  Host            $host;
    proxy_set_header  X-Real-IP       $remote_addr;
    proxy_set_header  X-Forwarded-For $proxy_add_x_forwarded_for;
 }
}

#!/bin/sh #Tomcat init script
HOME=/home/dhis/tomcat/bin
case $1 in start) sh ${HOME}/startup.sh ;; stop) sh ${HOME}/shutdown.sh ;; restart) sh ${HOME}/shutdown.sh sleep 5 sh ${HOME}/startup.sh ;; esac exit 0

#!/bin/sh #nginx init script
DAEMON=/usr/local/nginx/sbin/nginx
case $1 in start) start-stop-daemon --start --exec $DAEMON ;; stop) start-stop-daemon --stop --exec $DAEMON ;; restart) start-stop-daemon --stop --exec $DAEMON sleep 1 start-stop-daemon --start --exec $DAEMON ;; esac exit 0

location ~ ^/api/(charts|maps|reports|reportTables|documents)/ {
  if ($request_method != GET) {
    return 405;
  }

  proxy_pass        http://localhost:8080;
  proxy_redirect    off;
  proxy_set_header  Host              $host;
  proxy_set_header  X-Real-IP         $remote_addr;
  proxy_set_header  X-Forwarded-For   $proxy_add_x_forwarded_for;
  proxy_set_header  Authorization     "Basic YWRtaW46ZGlzdHJpY3Q=";
  proxy_set_header  Cookie            "";
  proxy_hide_header Set-Cookie;
}
